"use strict";function spiffingReplaceAll(str,find,replace){return str.replace(new RegExp(find,'g'),replace)}function spiffingProcessStylesheet(code){code=spiffingReplaceAll(code,"colour","color");code=spiffingReplaceAll(code,"grey","gray");code=spiffingReplaceAll(code,"!please","!important");code=spiffingReplaceAll(code,"transparency","opacity");code=spiffingReplaceAll(code,"centre","center");code=spiffingReplaceAll(code,"photograph","image");code=spiffingReplaceAll(code,"capitalise","capitalize");console.log("Thank you for using SpiffingJS!");return code}function spiffingDeprocessStylesheet(code){code=spiffingReplaceAll(code,"color","colour");code=spiffingReplaceAll(code,"gray","grey");code=spiffingReplaceAll(code,"!important","!please");code=spiffingReplaceAll(code,"opacity","transparency");code=spiffingReplaceAll(code,"center","centre");code=spiffingReplaceAll(code,"image","photograph");code=spiffingReplaceAll(code,"capitalize","capitalise");console.log("Thank you for using SpiffingJS!");return code}